sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {


    const {
        data,
        state,
        setState,
        context
    } = api;

    const propNames = {
        propName1: 'x_962123_x_perfo_0.end_date'
    };

     const {
        snHttp
    } = helpers;

    // To calculate the remaining days to complete the review 
    const prompt_date = snHttp('/api/x_962123_x_perfo_0/prop_mgmt?property_name=' + propNames.propName1, {
        method: 'GET',
        batch: false,
    }).then(response => {

        var now = new Date();
        // returns review date in msec
        var review_date = Date.parse(response.response.result.end_date.toString());
        var diff = review_date - now.getTime();
        var noOfDays = Math.round(diff / (1000 * 60 * 60 * 24)).toFixed(0);
        if (noOfDays > 0)
            setState('moreDays', noOfDays);

    }).catch(error => {
        console.log(error.message);
    });

    // To calculate the totalRecCount
    const currentUser = api.context.session.user.sys_id;
    // stats is to do aggregate API, gets the count (sysparm_count)
    snHttp("/api/now/stats/x_962123_x_perfo_0_performance_review?sysparm_count=true&sysparm_query=active=true^manager.sys_id=" + currentUser, {
        method: "GET",
        batch: false,
    }).then(response => {
        if (response.response.result.stats.count > 0) {

            setState('totalRecCount', response.response.result.stats.count);

        }
    }).catch(error => {
        console.log('Error');
    });

    // calculate totalRecConfirmed State
    snHttp("/api/now/stats/x_962123_x_perfo_0_performance_review?sysparm_count=true&sysparm_query=active=true^state=confirmed^manager.sys_id=" + currentUser, {
        method: "GET",
        batch: false,
    }).then(response => {
        if (response.response.result.stats.count > 0) {

            setState('totalRecConfirmed', response.response.result.stats.count);

        }
    }).catch(error => {
        console.log('Error');
    });

    // calculate totalRecInProgress State
    snHttp("/api/now/stats/x_962123_x_perfo_0_performance_review?sysparm_count=true&sysparm_query=active=true^state!=confirmed^state !=draft^manager.sys_id=" + currentUser, {
        method: "GET",
        batch: false,
    }).then(response => {
        if (response.response.result.stats.count > 0) {

            setState('totalRecInProgress', response.response.result.stats.count);

        }
    }).catch(error => {
        console.log('Error');
    });
}
